import React from 'react'
import Autocomplete, { createFilterOptions } from '@material-ui/lab/Autocomplete';
import { TextField } from '@material-ui/core';
import { useHistory } from 'react-router-dom';
import cityList from '../city-list';


const filterOptions = createFilterOptions({
    matchFrom: 'start',
    limit: 50,
  });

function Search() {
    const history = useHistory();

    // const handleClick = (city, country) => {
    //     history.push('/weather/' + city + '/' + country)
    // }

    
    return (
        <Autocomplete
            id="combo-box"
            options={cityList}
            getOptionLabel={(option) => option.label}
            getOptionSelected={(option, value) => { if(option.val === value.id) { history.push(`/weather/${option.city}/${option.country}`) }} }
            style={{ width: 300 }}
            renderInput={(params) => <TextField {...params} label="Choose your destination" variant="outlined" />}
            filterOptions={filterOptions}
        />
    )
};

export default Search;
