import React from 'react'
import Autocomplete from 'react-autocomplete'
import { List, CellMeasurer, CellMeasurerCache } from 'react-virtualized';
import cityList from '../city-list';

export default class Search extends React.Component {
  constructor (props) {
    super()
    this.cellHeightCache = new CellMeasurerCache({
      defaultHeight: 42,
      fixedWidth: true
    })
    this.state = {
      searchingFor: '',
      data: cityList,
      selection: '',
    }
  }

  onSelect 
// item => this.setState({ selection: item }) console.log(item)
  renderItem = (item) => {
    return <div className='searchItem'>{item.name}</div>
  }

  renderMenu = (items, _, autocompleteStyle) => {
    this.cellHeightCache.clearAll()
    const rowRenderer = ({key, index, parent, style}) => {
      const Item = items[index]
      const onMouseDown = e => {
        if(e.button === 0) {
          Item.props.onClick(e)
        }
      }
      return (
        <CellMeasurer
          cache={this.cellHeightCache}
          key={key}
          parent={parent}
          rowIndex={index}
        >
          {React.cloneElement(Item, {
            style: style, 
            key: key, 
            onMouseEnter: null, 
            onMouseDown: onMouseDown
          })}
        </CellMeasurer>
      )
    }
    return (
      <List
        rowHeight={this.cellHeightCache.rowHeight}
        height={207}
        rowCount={items.length}
        rowRenderer={rowRenderer}
        width={autocompleteStyle.minWidth || 0}
        style={{
          //...customStyles,
          height: 'auto',
          maxHeight: '207px'
        }}
      />
    )
  }
  
  render () {
    const searchTerm = this.state.searchingFor;
    let data = searchTerm 
      ? this.state.data.filter(item =>
          item.name.toLowerCase().includes(searchTerm.toLowerCase())
        )
      : []
    return (
      <Autocomplete
        items={data}
        value={this.state.searchingFor}
        
        renderItem={this.renderItem}
        renderMenu={this.renderMenu}
        
        getItemValue={ item => item.name }
        onChange={(e, value)=> this.setState({searchingFor: value})}
        onSelect={this.onSelect}
      />
    )
  }
}
// //styling.css
