import WeatherDataService from '../../services/WeatherDataService';
// import queryString from 'query-string'
import '../../App.css';

const Weather = props => {
    const [country, setCountry] = useState("");
    const [city, setCity] = useState("");
    const [monthlyAverages, setMonthlyAverages] = useState([]);
    const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December']


    useEffect(() => {
        let city = getQueryVariable('city')
        let country = getQueryVariable('country')
        setCity(city);
        setCountry(country);
        WeatherDataService.getCityData(country, city)
            .then(response => {
                console.log(response.data)
                setMonthlyAverages(response.data);
            })
    }, [])

    // Get country and city from URL parameters
    const getQueryVariable = (variable) => {
        let query = window.location.search.substring(1);
        let vars = query.split("&");
        for (let i = 0; i < vars.length; i++) {
            let pair = vars[i].split("=")
            if (pair[0] === variable) {
                return pair[1]
            }
        }
    }

    return (
        <div className='weather-wrapper'>
            <span>Weather Page</span>
